mapper.select:
select

,

,

from userwithjob v inner join job j on v.jobid = j.id inner
join user u
on
v.userid = u.id
where v.jobid in (select id from job


		and companyid=#{companyId}


)

	and v.userid = #{userId}


	and j.name like #{jobname}


	and u.realname like #{realname}


	order by ${sort} ${order}


	limit #{start},#{size}

select count(*)
from userwithjob v inner join job j on v.jobid = j.id
inner
join user u
on v.userid = u.id
where v.jobid in (select id from job


		and companyid=#{companyId}


)

	and v.userid = #{userId}


	and j.name like #{jobname}


	and u.realname like #{realname}

select count(*)
from userwithjob v inner join job j on v.jobid = j.id
inner
join user u
on v.userid = u.id
where v.jobid in (select id from job


		and companyid=#{companyId}


) and v.success=1

	and v.userid = #{userId}


	and j.name like #{jobname}


	and u.realname like #{realname}

select

,

,

,com.name as comName
from userwithjob v
inner join job j on v.jobid = j.id
inner join user u on v.userid = u.id
inner join company com on j.companyid= com.id
where v.jobid in (select id from job


		and companyid=#{companyId}


) and com.name in(select name from company where id = #{companyId}) and v.success=1

	and v.userid = #{userId}


	and j.name like #{jobname}


	and u.realname like #{realname}


	order by ${sort} ${order}


	limit #{start},#{size}

select

,

,

,com.name as comName
from userwithjob v
inner join job j on v.jobid = j.id
inner join user u on v.userid = u.id
inner join company com on j.companyid= com.id
where v.userid in (select id from user


		and id=#{id}


) and com.name in(select name from company where id = #{companyId}) and v.success=1

	and v.userid = #{userId}


	and j.name like #{jobname}


	and u.realname like #{realname}


	order by ${sort} ${order}


	limit #{start},#{size}
